(mkdir cinit;
cd cinit;
cat << EOF > user-data
#cloud-config
no_ssh_fingerprints: true
users:
  - name: alpine
    groups: users, wheel
    sudo: ALL=(ALL) NOPASSWD:ALL
    shell: /bin/bash
    ssh_authorized_keys:
      - ssh-rsa  mykey@host
  - name: timmy
    groups: users, wheel
    sudo: ALL=(ALL) NOPASSWD:ALL
    shell: /bin/bash
    ssh_authorized_keys:
      - ssh-rsa  mykey@host
ssh:
  emit_keys_to_console: false
ssh_authorized_keys:
  - ssh-rsa  mykey@host
password: myuserpassfortesting11
chpasswd:
  expire: false
  users:
  - {name: timmy, password: password111, type: text}
runcmd:
  - echo 'running cloud-init' 
  - echo 'First command executed successfully!' >> /run/testing.txt
  - [ sh, -c, "apk add tor tsocks screen curl socat bash sudo" ]
  - [ sh, -c, "echo 'Second command executed successfully!' >> /run/testing.txt" ]
  - [ sh, -c, "echo 'First command executed successfully!' >> /dev/ttyS0 || true" ]
